import React, { Component } from 'react'

export default class Register extends Component {
    constructor(props){
        super(props);
        this.state = {
            redirect: null
            }
        }
    handleSubmit = (e) =>{
        e.preventDefault();

        let myHeaders = new Headers();
        myHeaders.append('Content-Type', 'application/json')

        let password = e.target.password.value
        let confirmPass = e.target.confirmPass.value
        if(password !== confirmPass){
            this.setState({
                redirect: '/register'
            })
        }

        let data = JSON.stringify({

            username: e.target.username.value,
            email: e.target.email.value,
            password: e.target.password.value
        })

        fetch('http://localhost:5000/api/users', {
            method: 'POST',
            headers: myHeaders,
            body: data
        }).then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({
                    redirect: '/users'
                })
            })
            .catch(err => console.log(err))
    }



    render() {
        return (
            <div>
            <h3 className='text-center pt-4 pb-3'>Registration:</h3>
                 {/* this.state.redirect ? <Redirect to={this.state.redirect} /> : */}
            <form style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
            }}>
                 {/* onSubmit={this.handleSubmit} */}
                <div className='form-group col-9'>

                    <fieldset>
                        <label htmlFor='first_name'>First Name:</label>
                        <input type='text' name='first_name' className='form-control' placeholder='First Name' />
                    </fieldset>

                    <fieldset>
                        <label htmlFor='last_name'>Last Name:</label>
                        <input type='text' name='last_name' className='form-control' placeholder='Last Name' />
                    </fieldset>

                    <fieldset>
                        <label htmlFor='email'>Email:</label>
                        <input type='text' name='email' className='form-control' placeholder='Email' />
                    </fieldset>

                    <fieldset>
                        <label htmlFor='username'>Username:</label>
                        <input type='text' name='username' className='form-control' placeholder='Username' />
                    </fieldset>

                    <fieldset>
                        <label htmlFor='password'>Password:</label>
                        <input type='password' name='password' className='form-control' placeholder='Password' />
                    </fieldset>

                    <fieldset>
                        <label htmlFor='confirmPass'>Confirm Password:</label>
                        <input type='password' name='confirmPass' className='form-control' placeholder='Confirm Password' />
                    </fieldset>
                    <br />
                    <input type='submit' className='btn btn-dark' />

                    <div className='col'>
                    <img src="https://studio.uxpincdn.com/studio/wp-content/uploads/2020/12/7-Best-Reasons-To-Use-React.js-Components-In-Your-Project-1024x512.png.webp" className='image-fluid rounded mx-auto d-block' width='500' />
                    </div>
                </div>
            </form>
         </div>
        )
    }
}
