import React, { Component } from 'react'
import { Link } from 'react-router-dom'

export default class ProductCard extends Component {
    constructor(props){
        super(props)
        this.state = {
            shoppingCart: [] 
        }
    }
    addItem = (product) => {
        let myHeaders = new Headers()
        myHeaders.append('Authorization', `Bearer ${localStorage.getItem('token')}`)

        fetch('https://kekambas-bs.herokuapp.com/api/my-cart', {
            method: 'POST',
            headers: myHeaders,
        }).then(res => res.json())
            .then(data => {
                this.setState({
                    shoppingCart: 1
                })
            })
     }



    render() {
        return (
            <div className="card w-25 text-white bg-dark m-3 border-info">
            <img src={this.props.product.image} className="card-img-top" alt="product" />
                <div className="card-body">
                    <h4 className="card-title">{this.props.product.name}</h4>
                    <h5>${this.props.product.price}</h5>
                    <p className="card-text">Click View Item for a </p>
                    <Link to={`/products/${this.props.product.id}`} className="btn btn-sm btn-outline-info text-white">View Product</Link>
                    <br /> <br />
                    <Link to={'/my-cart'} onClick={this.addItem} className="btn btn-sm btn-outline-success text-white">Add to Cart</Link>
                </div>
            </div>


        )
    }
}
