import React, { Component, useEffect, useState } from 'react'

export const ProductDetails = (props) => {
    const [product, setProduct] = useState({});
    const productId = props.match.param.id;

    useEffect(() => {
        fetch(`https://kekambas-bs.herokuapp.com/api/products/${productId}`)
        .then(res => res.json())
        .then(data => setProduct(data))
    }, [])
    return(
        <div className="card w-25 text-white bg-dark m-3">
        <img src={product.image} className="card-img-top" alt="product" />
            <div className="card-body">
                <h4 className="card-title">{this.props.product.name}</h4>
                <h5>${this.props.product.price}</h5>
                <p className="card-text">{this.props.product.description}</p>
                <Link to="/" className="btn btn-sm btn-outline-secondary text-white">View Product</Link>
            </div>
        </div>

    )
}
